
public class Operator2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * 복합 대입 연산자
		 */
		int n = 13;
		
		n+=4;
		System.out.println(n);
		
		n-=2;
		System.out.println(n);
		
		n*=3;
		System.out.println(n);
		
		n/=5;
		System.out.println(n);
		
		n%=3;
		System.out.println(n);
	}

}
