
public class Operator6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 15;
		int n2 = 10;
		System.out.println(n1 & n2);
		System.out.println(n1 | n2); //논리는 ||
		System.out.println(n1 ^ n2); //XOR 연산 ^
		System.out.println(~ n2); // NOT 연산 ~
		System.out.println(n2 >> 1); //2로 나눈다.
		System.out.println(n2 >> 2);
		System.out.println(n2 >> 3);
		System.out.println(n2 << 1); //2를 곱한다.
		System.out.println(n2 << 2);
		System.out.println(n2 << 3);
		
	}

}
